import React from "react";
import "./Buttons.css";
import { Link } from "react-router-dom";

const STYLES = ["btn--primary", "btn--outline"];
const SIZE = ["btn--medium", "btn--large"];

const Button = ({ 
    children, 
    type, 
    onClick, 
    buttonStyle, 
    buttonSize, 
    to, 
    buttonType // New prop for sign-up or login
}) => {
  const buttonStyles = STYLES.includes(buttonStyle) ? buttonStyle : STYLES[0];
  const buttonsSize = SIZE.includes(buttonSize) ? buttonSize : SIZE[0];

  return (
    <Link to={to} className="btn-mobile">
      <button
        className={`btn ${buttonStyles} ${buttonsSize} ${buttonType}`} // Added class
        onClick={onClick}
        type={type}
      >
        {children}
      </button>
    </Link>
  );
};

export default Button;
